mapper.select:
select

from t_sys_log
where 1 = 1

    and id = #{id,jdbcType=BIGINT}


    and log_url = #{logUrl,jdbcType=VARCHAR}


    and log_class = #{logClass,jdbcType=VARCHAR}


    and log_method = #{logMethod,jdbcType=VARCHAR}


    and log_param = #{logParam,jdbcType=VARCHAR}


    and log_line = #{logLine,jdbcType=INTEGER}


    and log_lever = #{logLever,jdbcType=VARCHAR}


    and log_action = #{logAction,jdbcType=VARCHAR}


    and log_message = #{logMessage,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and is_delete = #{isDelete,jdbcType=TINYINT}


    and `status` = #{status,jdbcType=TINYINT}


    and `describe` = #{describe,jdbcType=VARCHAR}


    and sort = #{sort,jdbcType=BIGINT}
