mapper.select:
select

from t_ums_user
where 1 = 1

    and id = #{id,jdbcType=BIGINT}


    and username like concat('%',#{username,jdbcType=VARCHAR},'%')


    and nickname like concat('%',#{nickname,jdbcType=VARCHAR},'%')


    and is_locked = #{isLocked,jdbcType=BOOLEAN}


    and is_admin = #{isAdmin,jdbcType=BOOLEAN}


    and is_delete = #{isDelete,jdbcType=BOOLEAN}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and `status` = #{status,jdbcType=BOOLEAN}


    and `describe` = #{describe,jdbcType=VARCHAR}


    and sort = #{sort,jdbcType=BIGINT}
